DROP TABLE IF EXISTS "kanjidict";
DROP SEQUENCE IF EXISTS kanjidict_id_seq;
CREATE SEQUENCE kanjidict_id_seq INCREMENT 1 MINVALUE 1 MAXVALUE 2147483647 CACHE 1;

CREATE TABLE "public"."kanjidict" (
    "kanji" text NOT NULL,
    "onyomi" text NOT NULL,
    "kunyomi" text NOT NULL,
    "strokes" integer NOT NULL,
    "grade" text NOT NULL,
    "jlpt" text NOT NULL,
    "frequency" integer,
    "meaning" text NOT NULL,
    "id" integer DEFAULT nextval('kanjidict_id_seq') NOT NULL,
    CONSTRAINT "kanjidict_pkey" PRIMARY KEY ("id")
) WITH (oids = false);

